<?php/********************************************class.routes.php*******************Created by HUW*********************Created on 28-04-2014**************Modified on **************************************************************************************/class routes{	public $page;	public $uri;	public $config;	function __construct()	{		$this->route();		}
	function route()	{		if(isset($this->uri[1]))		{			$final=explode('?',trim($this->uri[1]));			if($final[0]!='' && $final[0]!=null)			{				$action=$final[0];				$arg_array=array();				$i=0;				foreach($this->uri as $uris)				{					if($i!=0 && $i!=1)					{						if($uris)						{							$uris=explode('?',trim($uris));							$arg_array[]=$uris[0];													}					}										$i++;				}				call_user_func_array(array($this->page,$action),$arg_array); 			}else			{				$this->page->index();			}		}else		{			$this->page->index();		}	}	function uri($key,$full=false)	{		$segment=$_SERVER['SCRIPT_NAME'];		if (preg_match("/\bindex.php\b/i",$_SERVER['REQUEST_URI'] ))			$segment=explode($segment.'/',$_SERVER['REQUEST_URI']);		else			$segment=explode(preg_replace('@/+$@','',dirname($segment)).'/',$_SERVER['REQUEST_URI']);				$segment=explode('/',$segment[1]);				if(!$full)		{			if(isset($segment[$key]))			{				$final=explode('?',trim($segment[$key]));				return $final[0];			}else			{				return "";			}		}else		{			return $segment;		}	}}?>